import { useEffect, useContext } from "react";
import { useHistory } from "react-router-dom";
import { Row, Col, Button, Spin } from "antd";
import { LoadingOutlined } from "@ant-design/icons";
import { createOrder, resetOrder, requestOrderDetail } from "../actions";
import { StoreContext } from "../store";

export default function PlaceOrderCard() {
  const {
    state: {
      cart,
      userSignin: { userInfo },
      orderInfo: { loading, success, order },
    },
    dispatch,
  } = useContext(StoreContext);
  const { cartItems } = cart;
  const history = useHistory();
  const antIcon = (
    <LoadingOutlined style={{ fontSize: 80, color: "#8183ff" }} spin />
  );

  const placeOrderHandler = () => {
    createOrder(dispatch, cart, userInfo);
    // history.push("/order");
  };

  const getTotalPrice = () => {
    return cartItems.length > 0
      ? cartItems.reduce((sum, item) => sum + item.price * item.qty, 0)
      : 0;
  };

  const toPrice = (num) => Number(num.toFixed(2)); // 5.123 => "5.12" => 5.12
  cart.itemsPrice = toPrice(
    cart.cartItems.reduce((a, c) => a + c.qty * c.price, 0)
  );
  cart.shippingPrice = cart.itemsPrice > 100 ? toPrice(0) : toPrice(10);
  cart.taxPrice = toPrice(0.15 * cart.itemsPrice);
  cart.totalPrice = cart.itemsPrice + cart.shippingPrice + cart.taxPrice;

  useEffect(() => {
    if (success) {
      resetOrder(dispatch);
      // requestOrderDetail(dispatch, order.id)
      history.push(`/order/${order.id}`);
    }
  }, [success]);

  return (
    <>
      {loading ? (
        <div className="spinner-wrap">
          <Spin indicator={antIcon} className="spinner" />
        </div>
      ) : (
        <Row gutter={[24, 24]}>
          <Col xs={{ span: 20, offset: 2 }} lg={{ span: 13, offset: 2 }}>
            <div className="card card-body">
              <h2 style={{ color: "white" }}>Shipping</h2>
              <p>
                <strong>Name:</strong> {cart.shippingAddress.fullName} <br />
                <strong>Address: </strong> {cart.shippingAddress.address},
                {cart.shippingAddress.city}, {cart.shippingAddress.postalCode},
                {cart.shippingAddress.country}
              </p>
            </div>
            <div className="card card-body">
              <h2 style={{ color: "white" }}>Payment</h2>
              <p>
                <strong>Method:</strong> {cart.paymentMethod}
              </p>
            </div>
            <div className="card card-body">
              <h2 style={{ color: "white" }}>Order Items</h2>
              {cartItems.length === 0 ? (
                <div>Cart is empty</div>
              ) : (
                cartItems.map((item) => (
                  <li key={item.id} className="cart-item">
                    <div className="cart-image">
                      <img src={item.image} alt={item.name} />
                    </div>
                    <div className="cart-item-content">
                      <div className="cart-name">{item.name}</div>
                      <div className="product-qty">Qty: {item.qty}</div>
                    </div>
                    <div className="cart-item-end">
                      <div className="cart-price">${item.price * item.qty}</div>
                    </div>
                  </li>
                ))
              )}
              <div className="cart-total-price-wrap">
                Total
                <div className="cart-total-price">${getTotalPrice()}</div>
              </div>
            </div>
          </Col>
          <Col xs={{ span: 20, offset: 2 }} lg={{ span: 7, offset: 0 }}>
            <div className="card card-body">
              <h2 style={{ color: "white" }}>Order Summary</h2>
              <div className="row">
                <div>Items</div>
                <div>${cart.itemsPrice}</div>
              </div>
              <div className="row">
                <div>Shipping</div>
                <div>${cart.shippingPrice}</div>
              </div>
              <div className="row">
                <div>Tax</div>
                <div>${cart.taxPrice}</div>
              </div>
              <div className="row">
                <div>
                  <strong> Order Total</strong>
                </div>
                <div>
                  <strong>${cart.totalPrice}</strong>
                </div>
              </div>
              <Button
                className="primary-btn"
                block
                type="primary"
                onClick={placeOrderHandler}
              >
                Place Order
              </Button>
            </div>
          </Col>
        </Row>
      )}
    </>
  );
}
